mapper.update:
UPDATE records 
SET 
    diet = #{diet},
    workout = #{workout},
    is_shared_with_trainer = #{isSharedWithTrainer}
WHERE record_id = #{recordId};

mapper.select:
SELECT * 
FROM records 
WHERE user_id = #{userId}
ORDER BY record_date DESC;

SELECT following_id 
FROM follows 
WHERE user_id = #{userId};
